import React, { useState } from 'react'

const App = () => {
  const [ persons, setPersons] = useState([
    { name: 'Arto Hellas' }
  ]) 
  const [ newName, setNewName ] = useState('')
  const [ nameAlready, setNameAlready] = useState(false)

  const setToNameAlready = value => {
    setNameAlready(value)
  }

  const addPerson = (event) => {
    event.preventDefault()
    const personObject = {
      name: newName
    }
    persons.forEach(person => {
      console.log("person.name: ",person.name," personObject.name: ",personObject.name)
      console.log("2: ",person.name == personObject.name)
      console.log("3: ",person.name === personObject.name)
      if(person.name === personObject.name){

        setToNameAlready(true)
        console.log("vaihtuuko true",nameAlready)

      }
    })
    if(nameAlready){
      alert(`vaihtuuko trueksi ${nameAlready}`)
      setToNameAlready(false)
    } else {
      setPersons(persons.concat(personObject))
      setNewName('')
    }
  }

  const handlePersonChange = (event) => {
    setNewName(event.target.value)
  }

  const rows = () => persons.map(person => <div key={person.name}>{person.name}</div>)


  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addPerson}>
        <div>
          name: <input value={newName} onChange={handlePersonChange}/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {rows()}
    </div>
  )

}

export default App
